import React, {Component} from 'react';
import './Sql-injection-form.css';

class SqlInjectionForm extends Component {


  selectUser() {
    api.post('/add/topic', topic)
    .then(response => {
      if (response.status === 200) {
        topic.id = response.data.insertId;

        this.props.addTopic({item: topic});
      }
    })
    .catch(error => console.log(error));
  }

  render() {
    var classes = ['sql-injection-form-container', this.props.type];
    return (
      <div className={classes.join(' ')}>
        <input className='sql-injection-input' type='text' placeholder='User Id'></input>
        <button className='sql-injection-btn' onClick={() => this.selectUser()}>Fetch it</button>
      </div>
    );
  }
}

export default SqlInjectionForm;
