import React, {Component} from 'react';
import './Tab.css';

class Tab extends Component {
  state = {
    isActive: false
  }

  handleTabSelection() {
    this.setState({isActive: !this.state.isActive && true});
    console.log('aasdasdasd', this.state.isActive)
  }

  render() {
    var tabContentClasses = ['tab-content', this.state.isActive ? 'true' : ''];

    return (
      <div className="tab-container">
        <h5 className='tab-title' onClick={() => this.handleTabSelection()}>{this.props.tab}</h5> 
        <div className={tabContentClasses.join(' ')}>sdfsdfsdf</div>
      </div>
    );
  }
}

export default Tab;
