import React, {Component} from 'react';
import _ from 'lodash';
import SqlInjection from '../contents/sql-injection/Sql-injection';
import NoSqlInjection from '../contents/noSql-injection/NoSql-injection';
import OsInjection from '../contents/os-injection/Os-injection';
import LdapInjection from '../contents/ldap-injection/Ldap-injection';
import CrossSiteScripting from '../contents/cross-site-scripting/Cross-site-scripting';
import InsecureDeserialization from '../contents/insecure-deserialization/Insecure-deserialization';
import Content from '../contents/content/Content';
import Tab from './tab/Tab';

import './Tabs.css';

class Tabs extends Component {
  state = {
    contents: {
      SqlInjection: true,
      NoSqlInjection: false, 
      OSInjection: false,
      LDAPInjection: false,
      CrossSiteScripting: false,
      InsecureDeserialization: false
    }
  }

  handleTabSelect({content}) {
    var contents = {...this.state.contents};

    var newContents = _.map(Object.keys(contents), c => {
      return {[c]: c === content ? true : false }
    });



    console.log(newContents)
    // this.setState({contents: {...this.state.content, content}});
  }

  render() {
    var tabs = [
      {key: 'SqlInjection', value: 'SQL Injection'},
      {key: 'NoSqlInjection', value: 'NoSQL Injection'},
      {key: 'OSInjection', value: 'OS Injection'},
      {key: 'LDAPInjection', value: 'LDAP Injection'}, 
      {key: 'CrossSiteScripting', value: 'Cross-site scripting'}, 
      {key: 'InsecureDeserialization', value: 'Insecure deserialization'}
    ];

    return (
      <div className="tabs-container">
        <div className='tabs'>
          {_.map(tabs, tab => <Tab tab={tab} handleSelect={({content}) => this.handleTabSelect({content})}/>)}
        </div>
        <Content>
          <SqlInjection isActive={this.state.contents.SqlInjection}/>
          <NoSqlInjection isActive={this.state.contents.NoSqlInjection}/>
          <OsInjection isActive={this.state.contents.OsInjection}/>
          <LdapInjection isActive={this.state.contents.LdapInjection}/>
          <CrossSiteScripting isActive={this.state.contents.CrossSiteScripting}/>
          <InsecureDeserialization isActive={this.state.contents.InsecureDeserialization}/>
        </Content>
      </div>
    );
  }
}

export default Tabs;
